        init: function() {
            this.bindElments();
            this.bindEvents();
            this._initHighlighter();

            this.log('EReader init finished...');
        },

        bindEvents: function() {
            var _this = this;
            $(window).on('touchend', function(e) {
                _this.log("callback://");   
             });
        },

        bindElments: function() {
            this.$content = $('#content');
            this.$header = $('#header');
            this.$footer = $('#footer');
        },

